mapper.select:
select id from ysh_enterprise_func where
catalog >= #{minCatalog,jdbcType=BIGINT}
and catalog <= #{maxCatalog,jdbcType=BIGINT}

mapper.update:
update ysh_enterprise_func
set undistributed_price = #{backSubsidyPrice,jdbcType=DECIMAL}+undistributed_price
where id = #{id,jdbcType=BIGINT}
