mapper.delete:
delete from ysh_enterprise_authority_page
where authority_id = #{authorityId,jdbcType=BIGINT}
and page_id = #{pageId,jdbcType=BIGINT}

mapper.select:
select count(*)
from ysh_enterprise_authority_page
where authority_id = #{authorityId,jdbcType=BIGINT}
and page_id = #{pageId,jdbcType=BIGINT}

select authority_id
from ysh_enterprise_authority_page
where page_id = #{pageId,jdbcType=BIGINT}
