mapper.select:
select
#{enterpriseId,jdbcType=BIGINT} as enterprise_id,
(SELECT enterprise_name from  ysh_enterprise_info where enterprise_id=#{enterpriseId,jdbcType=BIGINT})as enterprise_name,
(SELECT end from  ysh_enterprise_func where id=#{enterpriseId,jdbcType=BIGINT}) as end,
aa.ref customer_ref,
COUNT(DISTINCT(aa.customer_id)) as customer_amount,
count(DISTINCT(
CASE
when aa.grand_frequency>0 then aa.customer_id
end)
) as grand_customer_amount,
sum(aa.grand_price) as grand_price,
count(DISTINCT(
CASE
when aa.decut_frequency>0 then aa.customer_id
end)
) as decut_customer_amount,
-sum(aa.decut_price) as decut_price,
count(DISTINCT(
CASE
when aa.consume_frequency>0 then aa.customer_id
end)
) as consume_customer_amount,
sum(aa.consume_price) as consume_price,

count(DISTINCT(
CASE
when aa.consume_subsidy_price_offline is not NULL then aa.customer_id
end)
) as consume_subsidy_customer_amount,
sum(aa.consume_subsidy_price_offline)as consume_subsidy_price,
sum(aa.consume_settel_price) as consume_settle_price,
sum(aa.refund_settel_price) as refund_settle_price,
sum(aa.current_price) as current_price
from
(select
customer_enterprise.customer_id,
CASE
WHEN

    (
    SELECT
    create_time
    FROM
    ysh_custom_ucion_history
    WHERE
    customer_id = customer_enterprise.customer_id
    AND enterprise_id IN (
    SELECT
    id
    FROM
    ysh_enterprise_func
    where
    catalog >= #{minCatalog,jdbcType=BIGINT}
    and catalog <= #{maxCatalog,jdbcType=BIGINT}
    )

         AND create_time<#{end,jdbcType=TIMESTAMP} 

    ORDER BY
    create_time
    LIMIT 1
    )
    <#{start,jdbcType=TIMESTAMP} 

THEN
"old"
ELSE
"new"
END AS ref,
ifnull((SELECT balance_price from ysh_custom_ucion_history WHERE
customer_id = customer_enterprise.customer_id	AND enterprise_id = customer_enterprise.enterprise_id


       AND create_time<#{end,jdbcType=TIMESTAMP}


order by create_time desc,id desc limit 1),0.00) AS current_price,
count(CASE WHEN ysh_custom_ucion_history.type = 'ENTERPRISE_GRAND'


    and ysh_custom_ucion_history.create_time>=#{start,jdbcType=TIMESTAMP}




and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}


THEN	ysh_custom_ucion_history.id	END	) AS grand_frequency,
sum(CASE WHEN ysh_custom_ucion_history.type = 'ENTERPRISE_GRAND'


    and ysh_custom_ucion_history.create_time>=#{start,jdbcType=TIMESTAMP}




and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}


THEN	ysh_custom_ucion_history.price END) AS grand_price,
count(CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_DEDUCT'


    and ysh_custom_ucion_history.create_time>=#{start,jdbcType=TIMESTAMP}




and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}


THEN	ysh_custom_ucion_history.id	END) AS decut_frequency,
sum(CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_DEDUCT'


    and ysh_custom_ucion_history.create_time>=#{start,jdbcType=TIMESTAMP}




and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}


THEN	ysh_custom_ucion_history.price END) AS decut_price,
COUNT(CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_CONSUME'


    and ysh_custom_ucion_history.create_time>=#{start,jdbcType=TIMESTAMP}




and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}


THEN ysh_custom_ucion_history.id END) AS consume_frequency,
sum(CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_CONSUME'


    and ysh_custom_ucion_history.create_time>=#{start,jdbcType=TIMESTAMP}




and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}


THEN ysh_custom_ucion_history.price	END) AS consume_price,
sum(CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_CONSUME'	AND np_order.is_valet = 1 THEN	np_order.subsidy_price END) AS consume_subsidy_price_offline,
sum(CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_CONSUME'


    and ysh_custom_ucion_history.create_time>=#{start,jdbcType=TIMESTAMP}




and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}


THEN ysh_custom_ucion_history.pay_settle_price	END) AS consume_settel_price,
sum(CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_REFUND'


    and ysh_custom_ucion_history.create_time>=#{start,jdbcType=TIMESTAMP}




and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}


THEN	ysh_custom_ucion_history.pay_settle_price	END) AS refund_settel_price
from customer_enterprise
left join ysh_enterprise_func
on customer_enterprise.enterprise_id = ysh_enterprise_func.id
left join ysh_custom_ucion_history
on customer_enterprise.customer_id = ysh_custom_ucion_history.customer_id and customer_enterprise.enterprise_id = ysh_custom_ucion_history.enterprise_id
LEFT JOIN np_order
ON np_order.order_id = ysh_custom_ucion_history.order_id


    and np_order.create_time>=#{start,jdbcType=TIMESTAMP}




and np_order.create_time<=#{end,jdbcType=TIMESTAMP}





        and
        ysh_enterprise_func.catalog >= #{minCatalog,jdbcType=BIGINT}




        and
        ysh_enterprise_func.catalog <= #{maxCatalog,jdbcType=BIGINT}




        and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}



group by customer_enterprise.enterprise_id,customer_enterprise.customer_id)as aa
group by aa.ref

SELECT info.enterprise_id, info.enterprise_name AS parent_name, ysh_enterprise_info.enterprise_id AS current_id, ysh_enterprise_info.enterprise_name AS current_name, aa.customer_id, fullname, idCard_no, aa.ref customer_ref, aa.create_time, ifnull( ( SELECT balance_price FROM ysh_custom_ucion_history WHERE customer_id = aa.customer_id AND enterprise_id = aa.enterprise_id
AND create_time<#{start,jdbcType=TIMESTAMP}
ORDER BY create_time DESC,id desc LIMIT 1 ), 0.00 ) AS last_price, ifnull( ( SELECT balance_price FROM ysh_custom_ucion_history WHERE customer_id = aa.customer_id AND enterprise_id = aa.enterprise_id
AND create_time<#{end,jdbcType=TIMESTAMP}
ORDER BY create_time DESC, id desc LIMIT 1 ), 0.00 ) AS current_price, count( CASE WHEN ysh_custom_ucion_history.type = 'ENTERPRISE_GRAND' THEN ysh_custom_ucion_history.id END ) AS grand_frequency, sum( CASE WHEN ysh_custom_ucion_history.type = 'ENTERPRISE_GRAND' THEN ysh_custom_ucion_history.price END ) AS grand_price, count( CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_DEDUCT' THEN ysh_custom_ucion_history.id END ) AS decut_frequency, -sum( CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_DEDUCT' THEN ysh_custom_ucion_history.price END ) AS decut_price, COUNT( CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_CONSUME' AND np_order.is_valet = 1 THEN ysh_custom_ucion_history.id END ) AS consume_frequency_offline, sum( CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_CONSUME' AND np_order.is_valet = 1 THEN ysh_custom_ucion_history.price END ) AS consume_price_offline, sum( CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_CONSUME' AND np_order.is_valet = 1 THEN np_order.subsidy_price END ) AS consume_subsidy_price_offline, COUNT( CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_CONSUME' AND np_order.is_valet = 0 THEN ysh_custom_ucion_history.id END ) AS consume_frequency_online, sum( CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_CONSUME' AND np_order.is_valet = 0 THEN ysh_custom_ucion_history.price END ) AS consume_price_online, sum( CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_CONSUME' THEN ysh_custom_ucion_history.pay_settle_price END ) AS consume_settel_price, COUNT( CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_REFUND' THEN ysh_custom_ucion_history.id END ) AS refund_frequency, sum( CASE WHEN ysh_custom_ucion_history.type = 'UCOIN_REFUND' THEN ysh_custom_ucion_history.pay_settle_price END ) AS refund_settel_price FROM ( SELECT customer_enterprise.customer_id, customer_enterprise.enterprise_id, CASE WHEN
ysh_custom_ucion_history.create_time>=#{start,jdbcType=TIMESTAMP}
and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}
THEN "new" WHEN
ysh_custom_ucion_history.create_time<#{start,jdbcType=TIMESTAMP}
THEN "old" END AS ref, customer_enterprise.create_time FROM customer_enterprise LEFT JOIN ysh_enterprise_func ON customer_enterprise.enterprise_id = ysh_enterprise_func.id LEFT JOIN ysh_custom_ucion_history ON customer_enterprise.customer_id = ysh_custom_ucion_history.customer_id AND customer_enterprise.enterprise_id = ysh_custom_ucion_history.enterprise_id
and ysh_enterprise_func.catalog >= #{minCatalog,jdbcType=BIGINT}
and ysh_enterprise_func.catalog <= #{maxCatalog,jdbcType=BIGINT}
and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}
GROUP BY customer_enterprise.enterprise_id, customer_enterprise.customer_id ORDER BY ysh_enterprise_func.parent_id,ysh_enterprise_func.id
limit #{pageable.index,jdbcType=BIGINT}, #{pageable.length,jdbcType=BIGINT}
) AS aa
LEFT JOIN ysh_enterprise_func ON aa.enterprise_id = ysh_enterprise_func.id LEFT JOIN ysh_enterprise_info AS info ON ysh_enterprise_func.parent_id = info.enterprise_id LEFT JOIN chinapost_customer ON aa.customer_id = chinapost_customer.id LEFT JOIN ysh_enterprise_info ON aa.enterprise_id = ysh_enterprise_info.enterprise_id LEFT JOIN ysh_custom_ucion_history ON aa.customer_id = ysh_custom_ucion_history.customer_id AND aa.enterprise_id = ysh_custom_ucion_history.enterprise_id
and ysh_custom_ucion_history.create_time>=#{start,jdbcType=TIMESTAMP}
and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}
LEFT JOIN np_order ON np_order.order_id = ysh_custom_ucion_history.order_id
and np_order.create_time>=#{start,jdbcType=TIMESTAMP}
and np_order.create_time<=#{end,jdbcType=TIMESTAMP}
group by aa.enterprise_id,aa.customer_id

select count(*) from (select count(*) from customer_enterprise left join ysh_enterprise_func on customer_enterprise.enterprise_id = ysh_enterprise_func.id left join ysh_custom_ucion_history on customer_enterprise.customer_id = ysh_custom_ucion_history.customer_id and customer_enterprise.enterprise_id = ysh_custom_ucion_history.enterprise_id
and ysh_enterprise_func.catalog >= #{minCatalog,jdbcType=BIGINT}
and ysh_enterprise_func.catalog <= #{maxCatalog,jdbcType=BIGINT}
and ysh_custom_ucion_history.create_time<=#{end,jdbcType=TIMESTAMP}
group by customer_enterprise.enterprise_id,customer_enterprise.customer_id)aa

SELECT DATE_FORMAT(ce.create_time, '%m-%d') as every_day, count(ce.customer_id) AS new_customer_amount FROM customer_enterprise as ce LEFT JOIN ysh_enterprise_func as yef ON yef.id = ce.enterprise_id
and ce.create_time >= #{start,jdbcType=TIMESTAMP}
and ce.create_time <= #{end,jdbcType=TIMESTAMP}
and yef.catalog >= #{minCatalog,jdbcType=BIGINT}
and yef.catalog <= #{maxCatalog,jdbcType=BIGINT}
group by every_day order by ce.create_time

