select m.customer_id, m.idCard_no, m.fullname, m.total_consume_price, m.consume_amount, m.total_refund_price, m.refund_amount, sum(IFNULL((ysh_custom_ucion.rese_price),0)) as total_balance from( SELECT ids.customer_id, chinapost_customer.idCard_no, chinapost_customer.fullname, order_ids.total_consume_price, order_ids.consume_amount, refund_ids.total_refund_price, refund_ids.refund_amount FROM (SELECT DISTINCT(customer_id),enterprise_id ,create_time from customer_enterprise) as ids left join ( SELECT ysh_custom_ucion_history.customer_id, SUM(ifnull(ysh_custom_ucion_history.price,0)) as total_consume_price, count(ysh_custom_ucion_history.id) as consume_amount from ysh_custom_ucion_history
ysh_custom_ucion_history.type="UCOIN_CONSUME"
and ysh_custom_ucion_history.create_time >= #{start,jdbcType=TIMESTAMP}
and ysh_custom_ucion_history.create_time <= #{end,jdbcType=TIMESTAMP}
GROUP BY ysh_custom_ucion_history.customer_id) as order_ids on order_ids.customer_id=ids.customer_id left join (select ysh_custom_ucion_history.customer_id, SUM(ifnull(ysh_custom_ucion_history.price,0)) as total_refund_price, count(ysh_custom_ucion_history.id) as refund_amount FROM ysh_custom_ucion_history
ysh_custom_ucion_history.type="UCOIN_REFUND"
and ysh_custom_ucion_history.create_time >= #{start,jdbcType=TIMESTAMP}
and ysh_custom_ucion_history.create_time <= #{end,jdbcType=TIMESTAMP}
GROUP BY ysh_custom_ucion_history.customer_id) as refund_ids on refund_ids.customer_id =ids.customer_id left join chinapost_customer on chinapost_customer.id=ids.customer_id left join ysh_enterprise_func on ysh_enterprise_func.id=ids.enterprise_id
and ysh_enterprise_func.catalog >= #{minCatalog,jdbcType=BIGINT}
and ysh_enterprise_func.catalog <= #{maxCatalog,jdbcType=BIGINT}
and chinapost_customer.idCard_no=#{idCard,jdbcType=VARCHAR}
GROUP BY ids.customer_id order By ids.customer_id) as m left join ysh_custom_ucion on m.customer_id=ysh_custom_ucion.customer_id GROUP by m.customer_id
limit #{pageable.index,jdbcType=BIGINT}, #{pageable.length,jdbcType=BIGINT}

