and ysh_custom_ucion_history.create_time <= #{end,jdbcType=TIMESTAMP}
GROUP BY ysh_custom_ucion_history.enterprise_id)as zj on zj.enterprise_id=ysh_enterprise_func.id LEFT JOIN customer_enterprise ON ysh_enterprise_func.id = customer_enterprise.enterprise_id LEFT JOIN ysh_enterprise_info ON ysh_enterprise_info.enterprise_id = ysh_enterprise_func.id
ysh_enterprise_func.end=1
and ysh_enterprise_func.catalog >= #{minCatalog,jdbcType=BIGINT}
